import React, { useEffect } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Speakers from './components/Speakers';
import Workshop from './components/Workshop';
import EventDetails from './components/EventDetails';
import Countdown from './components/Countdown';
import Contact from './components/Contact';
import Footer from './components/Footer';

function App() {
  useEffect(() => {
    let lastScrollY = window.scrollY;
    
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const scrollDirection = currentScrollY > lastScrollY ? 'down' : 'up';
      const dots = document.querySelectorAll('.animated-dot');
      
      dots.forEach((dot, index) => {
        // Remove existing animation classes
        dot.classList.remove('dot-scroll-up', 'dot-scroll-down');
        
        // Add scroll direction animation with staggered timing
        setTimeout(() => {
          if (scrollDirection === 'down') {
            dot.classList.add('dot-scroll-down');
          } else {
            dot.classList.add('dot-scroll-up');
          }
        }, index * 50); // Stagger animations by 50ms
      });
      
      lastScrollY = currentScrollY;
    };

    // Throttle scroll events for better performance
    let ticking = false;
    const throttledScroll = () => {
      if (!ticking) {
        requestAnimationFrame(() => {
          handleScroll();
          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener('scroll', throttledScroll);
    
    return () => {
      window.removeEventListener('scroll', throttledScroll);
    };
  }, []);

  return (
    <div className="App">
      <Header />
      <Hero />
      <Countdown />
      <Speakers />
      <Workshop />
      <EventDetails />
      <Contact />
      <Footer />
    </div>
  );
}

export default App;
